queries.query:
(
  sum by(k8s_persistentvolumeclaim_name)(
    k8s_volume_capacity{k8s_namespace_name="$k8s_namespace_name", k8s_persistentvolumeclaim_name!=""}
    - k8s_volume_available{k8s_namespace_name="$k8s_namespace_name", k8s_persistentvolumeclaim_name!="", k8s_pod_name=~".*querier.*"}
  )
) / (
  sum by(k8s_persistentvolumeclaim_name)(
    k8s_volume_capacity{k8s_namespace_name="$k8s_namespace_name", k8s_persistentvolumeclaim_name!="", k8s_pod_name=~".*querier.*"}
  )
)

sum by(pod)(
  rate(container_fs_reads_bytes_total{
    container!= "",
    device=~"(/dev/)?(mmcblk[0-9]p[0-9]+|nvme.+|rbd.+|sd.+|vd.+|xvd.+|dm-.+|md.+|dasd.+)",
    namespace="$k8s_namespace_name", pod=~".*querier.*"
  }[$__rate_interval])
  +
  rate(container_fs_writes_bytes_total{
    container!= "",
    device=~"(/dev/)?(mmcblk[0-9]p[0-9]+|nvme.+|rbd.+|sd.+|vd.+|xvd.+|dm-.+|md.+|dasd.+)",
